import './App.css';
import axios from 'axios';
import { useState, useEffect } from 'react';
import RenderCoin from './components/RenderCoins';




function App() {
  const [data, setData] = useState([]); // array state 
  const [search, setSearch] = useState('');

  useEffect(() => {
    axios.get('https://api.coingecko.com/api/v3/coins/markets?vs_currency=usd&order=market_cap_desc&per_page=100&page=1&sparkline=false')
    .then (res => {
      setData(res.data);
    })
    .catch (err => {
      console.log('The URL is invalid or some other error occured');
    });

  }, []);

  const filteredData = data.filter(coin => { // array that contains filtered coins
    coin.name.toLowerCase().includes(search.toLowerCase())
  });

  const submitHandler = (event) => {
    setSearch(event.target.value);
  }

console.log(search);


  return (
    <div className="page">
      <div className='setting'>
        <h2>search</h2>
        <form> 
          <input type='text' onChange={submitHandler}/>
        </form>
      </div>

      {filteredData.map(coin => {
        return (
          <RenderCoin 
          key={coin.id} 
          coinName={coin.name} 
          coinSymbol={coin.symbol}
          coinPrice={coin.current_price}
          coinVolume={coin.market_cap}
          /> 
        )
      })}
      {/* <RenderCoin 
          key='3'
          coinName='sam'
          coinSymbol='{coin.symbol}'
          coinPrice='{coin.current_price}'
          coinVolume='{coin.market_cap}'
          />  */}
    </div>
  );
}

export default App;
